mapper.select:
SELECT iitem, `name`, price, pic
FROM item
WHERE iitem = #{iitem}

mapper.update:
UPDATE `order_item`
SET del_yn = 0
WHERE iorder = #{iorder}
AND iitem = #{iitem}

mapper.select:
SELECT iaddress, address, address_detail addressDetail, name, phone
FROM shipping_address
WHERE iuser = #{iuser}

SELECT iaddress, address, address_detail addressDetail, name, phone
FROM shipping_address
WHERE iuser = #{iuser}
AND iaddress = #{iaddress}

SELECT iorder
FROM order_item
WHERE iorder = #{iorder}
